            </div>
            <div className="tile">
              <div className="inner"></div>
            </div>
            {/* -------- Row 4 */}
            <div className="tile push">
              <div className="inner"></div>
            </div>
            <div className="tile">
              <div className="inner"></div>
            </div>
            <div className="tile white">
              <div className="inner">
                <p className="S"> </p>
              </div>
            </div>
            <div className="tile white">
              <div className="inner">
                <p className="W"> </p>
              </div>
            </div>
            <div className="tile white">
              <div className="inner">
                <p className="A"> </p>
              </div>
            </div>
            <div className="tile white">
              <div className="inner">
                <p className="N"></p>
              </div>
            </div>
            <div className="tile white">
              <div className="inner">
                <p className="S"> </p>
              </div>
            </div>
            <div className="tile white">
              <div className="inner">
                <p className="O"> </p>
              </div>
            </div>
            <div className="tile white">
              <div className="inner">
                <p className="N" />
              </div>
            </div>
            <div className="tile">
              <div className="inner"></div>
            </div>
            <div className="tile">
              <div className="inner"></div>
            </div>
            <div className="tile">
              <div className="inner"></div>
            </div>
          </div>
        </div>
        <h1>Score: {this.state.score}</h1>
        <button
          className={"reset-btn"}
          onClick={() => {
            this.checkGuess();
